$(function(){$(window).scroll(function(){$(this).scrollTop()>=90?($("nav.main-nav").addClass("stickytop"),$("#main-body").addClass("bggray")):($("nav.main-nav").removeClass("stickytop"),$("#main-body").removeClass("bggray"))})}),$(function(){$("a.page-scroll").bind("click",function(a){var o=$(this);$("html, body").stop().animate({scrollTop:$(o.attr("href")).offset().top},1500,"easeInOutExpo"),a.preventDefault()})});